let picker = document.getElementById("imagepicker");
let editor = picker.dataset.editor;
let setUrl;

switch (editor) {
    case "tinymce4":
        setUrl = function (url) {
            let params = top.tinymce.activeEditor.windowManager.getParams();
            let input = params.window.document.getElementById(params.input);
            input.value = url;
            if (input.onchange) {
                input.onchange();
            }
        };
        break;
    case "tinymce5":
        setUrl = function (url) {
            window.parent.postMessage({
                mceAction: 'setUrl',
                url: url
              }, '*');
        };
        break;
    case "ckeditor":
        setUrl = function (url) {
            let matches = location.search.match(/CKEditorFuncNum=(\d+)/);
            window.opener.CKEDITOR.tools.callFunction(matches[1], url);
            window.close();
        };
        break;
}

picker.onclick = function (event) {
    if (event.target instanceof HTMLImageElement) {
        setUrl(event.target.dataset.url);
        return false;
    }
}
